import { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { FileText, Plus, X } from 'lucide-react';
import { DashboardLayout } from '@/components/dashboard-layout';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useLocation } from 'wouter';

interface Medication {
  medicationName: string;
  dosage: string;
  frequency: string;
  duration: string;
  instructions: string;
}

export default function DoctorPrescribe() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [prescription, setPrescription] = useState({
    patientId: '',
    diagnosis: '',
    notes: '',
  });
  const [medications, setMedications] = useState<Medication[]>([
    { medicationName: '', dosage: '', frequency: '', duration: '', instructions: '' },
  ]);

  const createPrescriptionMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest('POST', '/api/prescriptions', data);
    },
    onSuccess: () => {
      toast({
        title: 'Prescription created',
        description: 'E-prescription has been verified on blockchain',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/prescriptions'] });
      queryClient.invalidateQueries({ queryKey: ['/api/doctor/stats'] });
      setLocation('/dashboard/doctor/prescriptions');
    },
    onError: () => {
      toast({
        variant: 'destructive',
        title: 'Failed to create prescription',
        description: 'Please try again',
      });
    },
  });

  const addMedication = () => {
    setMedications([...medications, { medicationName: '', dosage: '', frequency: '', duration: '', instructions: '' }]);
  };

  const removeMedication = (index: number) => {
    setMedications(medications.filter((_, i) => i !== index));
  };

  const updateMedication = (index: number, field: keyof Medication, value: string) => {
    const updated = [...medications];
    updated[index] = { ...updated[index], [field]: value };
    setMedications(updated);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createPrescriptionMutation.mutate({ prescription, medications });
  };

  return (
    <DashboardLayout role="doctor">
      <div className="space-y-6">
        <div>
          <h1 className="font-serif text-3xl font-bold">Create E-Prescription</h1>
          <p className="mt-2 text-muted-foreground">
            Write a blockchain-verified digital prescription
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="font-serif">Patient Information</CardTitle>
              <CardDescription>Enter patient ID and diagnosis</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="patientId">Patient ID</Label>
                <Input
                  id="patientId"
                  value={prescription.patientId}
                  onChange={(e) => setPrescription({ ...prescription, patientId: e.target.value })}
                  placeholder="Enter patient ID"
                  required
                  data-testid="input-patient-id"
                />
              </div>

              <div>
                <Label htmlFor="diagnosis">Diagnosis</Label>
                <Input
                  id="diagnosis"
                  value={prescription.diagnosis}
                  onChange={(e) => setPrescription({ ...prescription, diagnosis: e.target.value })}
                  placeholder="e.g., Hypertension, Type 2 Diabetes"
                  required
                  data-testid="input-diagnosis"
                />
              </div>

              <div>
                <Label htmlFor="notes">Clinical Notes (Optional)</Label>
                <Textarea
                  id="notes"
                  value={prescription.notes}
                  onChange={(e) => setPrescription({ ...prescription, notes: e.target.value })}
                  placeholder="Additional notes or instructions"
                  data-testid="textarea-notes"
                />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle className="font-serif">Medications</CardTitle>
                  <CardDescription>Add prescribed medications</CardDescription>
                </div>
                <Button type="button" variant="outline" size="sm" onClick={addMedication} data-testid="button-add-medication">
                  <Plus className="mr-2 h-4 w-4" />
                  Add Medication
                </Button>
              </div>
            </CardHeader>
            <CardContent className="space-y-6">
              {medications.map((med, index) => (
                <div key={index} className="rounded-md border p-4">
                  <div className="mb-4 flex items-center justify-between">
                    <h4 className="font-medium">Medication {index + 1}</h4>
                    {medications.length > 1 && (
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        onClick={() => removeMedication(index)}
                        data-testid={`button-remove-medication-${index}`}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    )}
                  </div>

                  <div className="grid gap-4 md:grid-cols-2">
                    <div>
                      <Label>Medication Name</Label>
                      <Input
                        value={med.medicationName}
                        onChange={(e) => updateMedication(index, 'medicationName', e.target.value)}
                        placeholder="e.g., Lisinopril"
                        required
                        data-testid={`input-medication-name-${index}`}
                      />
                    </div>

                    <div>
                      <Label>Dosage</Label>
                      <Input
                        value={med.dosage}
                        onChange={(e) => updateMedication(index, 'dosage', e.target.value)}
                        placeholder="e.g., 10mg"
                        required
                        data-testid={`input-dosage-${index}`}
                      />
                    </div>

                    <div>
                      <Label>Frequency</Label>
                      <Input
                        value={med.frequency}
                        onChange={(e) => updateMedication(index, 'frequency', e.target.value)}
                        placeholder="e.g., Once daily"
                        required
                        data-testid={`input-frequency-${index}`}
                      />
                    </div>

                    <div>
                      <Label>Duration</Label>
                      <Input
                        value={med.duration}
                        onChange={(e) => updateMedication(index, 'duration', e.target.value)}
                        placeholder="e.g., 30 days"
                        required
                        data-testid={`input-duration-${index}`}
                      />
                    </div>

                    <div className="md:col-span-2">
                      <Label>Instructions</Label>
                      <Textarea
                        value={med.instructions}
                        onChange={(e) => updateMedication(index, 'instructions', e.target.value)}
                        placeholder="e.g., Take with food"
                        data-testid={`textarea-instructions-${index}`}
                      />
                    </div>
                  </div>
                </div>
              ))}
            </CardContent>
          </Card>

          <div className="flex justify-end gap-2">
            <Button type="button" variant="outline" onClick={() => setLocation('/dashboard/doctor')}>
              Cancel
            </Button>
            <Button type="submit" disabled={createPrescriptionMutation.isPending} data-testid="button-submit-prescription">
              {createPrescriptionMutation.isPending ? 'Creating...' : 'Create Prescription'}
            </Button>
          </div>
        </form>
      </div>
    </DashboardLayout>
  );
}

server/health-insights.ts.content:
import { db } from './db';
import { healthInsights, medicalRecords } from '@shared/schema';

interface HealthData {
  bloodPressureSystolic?: number;
  bloodPressureDiastolic?: number;
  bloodGlucose?: number;
  cholesterolTotal?: number;
  cholesterolLDL?: number;
  heartRate?: number;
  temperature?: number;
  weight?: number;
  height?: number;
}

export class HealthInsightsAnalyzer {
  /**
   * Parse health data from file description or metadata
   * In a real system, this would use OCR/NLP on uploaded files
   */
  private static parseHealthData(description: string): HealthData {
    const data: HealthData = {};

    // Simple regex-based extraction (MVP - rule-based)
    const bpMatch = description.match(/BP:?\s*(\d+)\/(\d+)/i) || 
                   description.match(/blood pressure:?\s*(\d+)\/(\d+)/i);
    if (bpMatch) {
      data.bloodPressureSystolic = parseInt(bpMatch[1]);
      data.bloodPressureDiastolic = parseInt(bpMatch[2]);
    }

    const glucoseMatch = description.match(/glucose:?\s*(\d+)/i) ||
                        description.match(/blood sugar:?\s*(\d+)/i);
    if (glucoseMatch) {
      data.bloodGlucose = parseInt(glucoseMatch[1]);
    }

    const cholMatch = description.match(/cholesterol:?\s*(\d+)/i);
    if (cholMatch) {
      data.cholesterolTotal = parseInt(cholMatch[1]);
    }

    return data;
  }

  /**
   * Analyze health data and generate insights
   */
  static async analyzeRecord(recordId: string, patientId: string, description: string) {
    const healthData = this.parseHealthData(description);
    const insights: Array<{
      type: string;
      severity: string;
      title: string;
      description: string;
      recommendations: string;
    }> = [];

    // Blood Pressure Analysis
    if (healthData.bloodPressureSystolic && healthData.bloodPressureDiastolic) {
      if (healthData.bloodPressureSystolic >= 140 || healthData.bloodPressureDiastolic >= 90) {
        insights.push({
          type: 'alert',
          severity: 'high',
          title: 'Elevated Blood Pressure Detected',
          description: `Your blood pressure reading of ${healthData.bloodPressureSystolic}/${healthData.bloodPressureDiastolic} mmHg is above the normal range (120/80 mmHg).`,
          recommendations: 'Consult with your healthcare provider. Consider lifestyle modifications including reduced sodium intake, regular exercise, and stress management.',
        });
      } else if (healthData.bloodPressureSystolic >= 120 || healthData.bloodPressureDiastolic >= 80) {
        insights.push({
          type: 'recommendation',
          severity: 'medium',
          title: 'Pre-Hypertension Range',
          description: `Your blood pressure of ${healthData.bloodPressureSystolic}/${healthData.bloodPressureDiastolic} mmHg is in the pre-hypertension range.`,
          recommendations: 'Monitor your blood pressure regularly. Maintain a healthy diet and exercise routine.',
        });
      }
    }

    // Blood Glucose Analysis
    if (healthData.bloodGlucose) {
      if (healthData.bloodGlucose > 180) {
        insights.push({
          type: 'alert',
          severity: 'high',
          title: 'High Blood Sugar Level',
          description: `Blood glucose level of ${healthData.bloodGlucose} mg/dL is significantly elevated (normal: 70-140 mg/dL).`,
          recommendations: 'Immediate consultation with your doctor is recommended. Monitor your diet and consider diabetes screening.',
        });
      } else if (healthData.bloodGlucose > 140) {
        insights.push({
          type: 'recommendation',
          severity: 'medium',
          title: 'Elevated Blood Sugar',
          description: `Blood glucose of ${healthData.bloodGlucose} mg/dL is above normal range.`,
          recommendations: 'Consider reducing sugar intake and increasing physical activity. Schedule a follow-up test.',
        });
      } else if (healthData.bloodGlucose < 70) {
        insights.push({
          type: 'alert',
          severity: 'medium',
          title: 'Low Blood Sugar',
          description: `Blood glucose of ${healthData.bloodGlucose} mg/dL is below normal range.`,
          recommendations: 'Ensure regular meal times and adequate carbohydrate intake. Consult your healthcare provider.',
        });
      }
    }

    // Cholesterol Analysis
    if (healthData.cholesterolTotal) {
      if (healthData.cholesterolTotal > 240) {
        insights.push({
          type: 'alert',
          severity: 'high',
          title: 'High Cholesterol Level',
          description: `Total cholesterol of ${healthData.cholesterolTotal} mg/dL is above recommended levels (< 200 mg/dL).`,
          recommendations: 'Consult your doctor about cholesterol management. Consider dietary changes and regular exercise.',
        });
      } else if (healthData.cholesterolTotal > 200) {
        insights.push({
          type: 'recommendation',
          severity: 'medium',
          title: 'Borderline High Cholesterol',
          description: `Total cholesterol of ${healthData.cholesterolTotal} mg/dL is borderline high.`,
          recommendations: 'Focus on heart-healthy diet with reduced saturated fats. Increase fiber intake.',
        });
      }
    }

    // Save insights to database
    for (const insight of insights) {
      await db.insert(healthInsights).values({
        patientId,
        recordId,
        insightType: insight.type,
        severity: insight.severity,
        title: insight.title,
        description: insight.description,
        recommendations: insight.recommendations,
      });
    }

    return insights;
  }

  /**
   * Generate general health recommendations
   */
  static async generateGeneralRecommendations(patientId: string) {
    const generalInsights = [
      {
        type: 'recommendation',
        severity: 'low',
        title: 'Regular Health Checkups',
        description: 'Annual health screenings are important for early detection of potential health issues.',
        recommendations: 'Schedule yearly checkups with your primary care physician. Keep track of your vaccination schedule.',
      },
      {
        type: 'recommendation',
        severity: 'low',
        title: 'Maintain Healthy Lifestyle',
        description: 'A balanced diet and regular exercise are key to long-term health.',
        recommendations: 'Aim for 150 minutes of moderate exercise per week. Eat a variety of fruits, vegetables, and whole grains.',
      },
    ];

    for (const insight of generalInsights) {
      await db.insert(healthInsights).values({
        patientId,
        insightType: insight.type,
        severity: insight.severity,
        title: insight.title,
        description: insight.description,
        recommendations: insight.recommendations,
      });
    }

    return generalInsights;
  }
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer, boolean, jsonb } from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table with role-based access
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  email: text("email").notNull().unique(),
  fullName: text("full_name").notNull(),
  role: text("role").notNull(), // patient, doctor, pharmacy, insurance
  phone: text("phone"),
  specialization: text("specialization"), // for doctors
  licenseNumber: text("license_number"), // for doctors/pharmacies
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Medical records uploaded by patients
export const medicalRecords = pgTable("medical_records", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").notNull().references(() => users.id),
  title: text("title").notNull(),
  description: text("description"),
  fileType: text("file_type").notNull(), // pdf, image, etc
  fileUrl: text("file_url").notNull(),
  fileSize: integer("file_size").notNull(),
  recordType: text("record_type").notNull(), // lab_report, prescription, imaging, etc
  uploadedAt: timestamp("uploaded_at").defaultNow().notNull(),
});

// Access permissions for medical records
export const accessPermissions = pgTable("access_permissions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  recordId: varchar("record_id").notNull().references(() => medicalRecords.id),
  grantedToId: varchar("granted_to_id").notNull().references(() => users.id),
  grantedById: varchar("granted_by_id").notNull().references(() => users.id),
  accessLevel: text("access_level").notNull(), // view, download
  grantedAt: timestamp("granted_at").defaultNow().notNull(),
  revokedAt: timestamp("revoked_at"),
  isActive: boolean("is_active").default(true).notNull(),
});

// E-prescriptions created by doctors
export const prescriptions = pgTable("prescriptions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").notNull().references(() => users.id),
  doctorId: varchar("doctor_id").notNull().references(() => users.id),
  diagnosis: text("diagnosis").notNull(),
  notes: text("notes"),
  status: text("status").notNull().default('pending'), // pending, verified, dispensed
  dispensedById: varchar("dispensed_by_id").references(() => users.id),
  dispensedAt: timestamp("dispensed_at"),
  blockchainHash: text("blockchain_hash").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Individual medications in a prescription
export const prescriptionItems = pgTable("prescription_items", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  prescriptionId: varchar("prescription_id").notNull().references(() => prescriptions.id),
  medicationName: text("medication_name").notNull(),
  dosage: text("dosage").notNull(),
  frequency: text("frequency").notNull(),
  duration: text("duration").notNull(),
  instructions: text("instructions"),
});

// Insurance claims
export const insuranceClaims = pgTable("insurance_claims", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").notNull().references(() => users.id),
  agentId: varchar("agent_id").references(() => users.id),
  policyNumber: text("policy_number").notNull(),
  policyProvider: text("policy_provider").notNull(),
  claimAmount: integer("claim_amount").notNull(),
  claimType: text("claim_type").notNull(), // hospitalization, outpatient, pharmacy
  description: text("description").notNull(),
  status: text("status").notNull().default('submitted'), // submitted, under_review, approved, rejected, paid
  supportingDocuments: jsonb("supporting_documents").$type<string[]>().default([]),
  reviewNotes: text("review_notes"),
  blockchainHash: text("blockchain_hash").notNull(),
  submittedAt: timestamp("submitted_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Blockchain transaction log (immutable)
export const blockchainTransactions = pgTable("blockchain_transactions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  transactionHash: text("transaction_hash").notNull().unique(),
  actorId: varchar("actor_id").notNull().references(() => users.id),
  actionType: text("action_type").notNull(), // grant_access, revoke_access, create_prescription, verify_prescription, submit_claim
  resourceType: text("resource_type").notNull(), // medical_record, prescription, claim
  resourceId: varchar("resource_id").notNull(),
  metadata: jsonb("metadata").$type<Record<string, any>>().default({}),
  previousHash: text("previous_hash"),
  timestamp: timestamp("timestamp").defaultNow().notNull(),
});

// Health insights from AI analysis
export const healthInsights = pgTable("health_insights", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  patientId: varchar("patient_id").notNull().references(() => users.id),
  recordId: varchar("record_id").references(() => medicalRecords.id),
  insightType: text("insight_type").notNull(), // alert, recommendation, trend
  severity: text("severity").notNull(), // low, medium, high, critical
  title: text("title").notNull(),
  description: text("description").notNull(),
  recommendations: text("recommendations"),
  isRead: boolean("is_read").default(false).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  medicalRecords: many(medicalRecords),
  prescriptionsAsPatient: many(prescriptions, { relationName: "patientPrescriptions" }),
  prescriptionsAsDoctor: many(prescriptions, { relationName: "doctorPrescriptions" }),
  claims: many(insuranceClaims),
  blockchainTransactions: many(blockchainTransactions),
  healthInsights: many(healthInsights),
}));

export const medicalRecordsRelations = relations(medicalRecords, ({ one, many }) => ({
  patient: one(users, {
    fields: [medicalRecords.patientId],
    references: [users.id],
  }),
  accessPermissions: many(accessPermissions),
}));

export const prescriptionsRelations = relations(prescriptions, ({ one, many }) => ({
  patient: one(users, {
    fields: [prescriptions.patientId],
    references: [users.id],
    relationName: "patientPrescriptions",
  }),
  doctor: one(users, {
    fields: [prescriptions.doctorId],
    references: [users.id],
    relationName: "doctorPrescriptions",
  }),
  items: many(prescriptionItems),
}));

export const prescriptionItemsRelations = relations(prescriptionItems, ({ one }) => ({
  prescription: one(prescriptions, {
    fields: [prescriptionItems.prescriptionId],
    references: [prescriptions.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
});

export const insertMedicalRecordSchema = createInsertSchema(medicalRecords).omit({
  id: true,
  uploadedAt: true,
});

export const insertAccessPermissionSchema = createInsertSchema(accessPermissions).omit({
  id: true,
  grantedAt: true,
});

export const insertPrescriptionSchema = createInsertSchema(prescriptions).omit({
  id: true,
  createdAt: true,
  dispensedAt: true,
  dispensedById: true,
});

export const insertPrescriptionItemSchema = createInsertSchema(prescriptionItems).omit({
  id: true,
});

export const insertInsuranceClaimSchema = createInsertSchema(insuranceClaims).omit({
  id: true,
  submittedAt: true,
  updatedAt: true,
  agentId: true,
});

export const insertBlockchainTransactionSchema = createInsertSchema(blockchainTransactions).omit({
  id: true,
  timestamp: true,
});

export const insertHealthInsightSchema = createInsertSchema(healthInsights).omit({
  id: true,
  createdAt: true,
});

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type MedicalRecord = typeof medicalRecords.$inferSelect;
export type InsertMedicalRecord = z.infer<typeof insertMedicalRecordSchema>;

export type AccessPermission = typeof accessPermissions.$inferSelect;
export type InsertAccessPermission = z.infer<typeof insertAccessPermissionSchema>;

export type Prescription = typeof prescriptions.$inferSelect;
export type InsertPrescription = z.infer<typeof insertPrescriptionSchema>;

export type PrescriptionItem = typeof prescriptionItems.$inferSelect;
export type InsertPrescriptionItem = z.infer<typeof insertPrescriptionItemSchema>;

export type InsuranceClaim = typeof insuranceClaims.$inferSelect;
export type InsertInsuranceClaim = z.infer<typeof insertInsuranceClaimSchema>;

export type BlockchainTransaction = typeof blockchainTransactions.$inferSelect;
export type InsertBlockchainTransaction = z.infer<typeof insertBlockchainTransactionSchema>;

export type HealthInsight = typeof healthInsights.$inferSelect;
export type InsertHealthInsight = z.infer<typeof insertHealthInsightSchema>;

